import "./css/Contact.css"
import { useState } from "react";
import humanquestion from "./humanquestion.js";

function Contact({thanksfunction}) {

  // function input(event) {
  //   event.target.getAttribute("href")
  //   console.log(event.target)
  // }

  // console.log(e.tartget.value);

  // const [isLoggedIn, setIsLoggedIn] = useState(false);
  // const [username, setUsername] = useState('');


  // function onNav(event){
  //   event.preventDefault();
  //   const target = event.target.getAttribute("href");
  //   setPage(target);
  // }

  // const humanquestion = [

  //   { question: "please answer 1", answer: "1" },
  //   { question: "please answer 2", answer: "2" },
  //   { question: "please answer 3", answer: "3" },

  // ];

  function inputname(event) {
    setName(event.target.value)
    console.log(event.target.value)
  }

  // function setname() {
  //   setSaved(name)

  // }

  function inputemail(event) {
    setEmail(event.target.value)
    console.log(event.target.value)
  }

  function inputconfirm(event) {
    setConfirm(event.target.value)
    console.log(event.target.value)
  }

  function inputmessage(event) {
    setMessage(event.target.value)
    console.log(event.target.value)
  }

  function inputanswer(event) {
    setAnswer(event.target.value)
    console.log(event.target.value)
  }

  let setting ="false"
  function setinput() {
    setSavedname(name);
    setSavedemail(email);
    setSavedconfirm(confirm);
    setSavednumber(number);
    setSavedmessage(message);
    // {thanksfunction};
  }




  // const randomNumber = parseInt(Math.random() * number)

  function check(event) {
    event.preventDefault();

    reset()

    if (email) {
      if (email.includes("@")) {
        if (email === confirm)

          if (checkbox)
            if (answer == humanquestion[randomNumber].answer)
            setinput()
            else
            seterrorNohuman('you are not a human')

          else  setinput()
        // else
        //   setinput()
        // if (answer != humanquestion[randomNumber].answer)
        //   return seterrorNohuman('you are not a human');
        else

           seterrorNomatch("should be match")
      }
      else (
        seterrorNoat("@ is needed")
      )
    }
    else (
      seterrorNoemail("email is needed")
    )
  }

  // const [isDisable, setDisable] = useState('1');

  function reset() {
    seterrorNoemail('');
    seterrorNoat('');
    seterrorNomatch('');
    setrandomNumber(parseInt(Math.random() * maxvalue))
    seterrorNohuman('');
    // setDisable("false")
  }
  let maxvalue = 3
  const [randomNumber, setrandomNumber] = useState(parseInt(Math.random() * maxvalue));
  const [name, setName] = useState('');
  const [savedname, setSavedname] = useState('');

  const [number, setNumber] = useState('');
  const [savednumber, setSavednumber] = useState('');


  const [answer, setAnswer] = useState('');
  const [savedanswer, setSavedanswer] = useState('');


  const [email, setEmail] = useState('');
  const [savedemail, setSavedemail] = useState('');

  const [confirm, setConfirm] = useState('');
  const [savedconfirm, setSavedconfirm] = useState('');


  const [message, setMessage] = useState('');
  const [savedmessage, setSavedmessage] = useState('');

  // emailInput.value.includes(word)
  const [error, seterror] = useState('');
  const [success, setsuccess] = useState('');

  const [errorNoemail, seterrorNoemail] = useState('');
  const [errorNoat, seterrorNoat] = useState('');
  const [errorNomatch, seterrorNomatch] = useState('');

  const [errorNohuman, seterrorNohuman] = useState('');



  const [boxxxx, setbox] = useState('');
  const [checkbox, setcheckbox] = useState(false)


  function change() {
    setcheckbox(!checkbox)
  }


  let box = "close"
  if (checkbox) {
    box = "open"
    // setbox("nihenbang")
  }



  return (
    <div className="contact">
      <div className="column">
        <h2>Subscribe to the Latest News.</h2>
        Name is optional but email fields are required
        but we will need an email address if you would like a response. We do not keep or pass on email address.
        Please see our 'Cookies and Privacy' page for more details of our policy on the use of email addresses.
        <div>
          <h3>Name and email fields are required</h3>
          <p>The email should be included the "@"</p>
          <p>We do not keep or pass on email address.</p>


          {/* <button type="submit"  onClick={thanksfunction} >SUBMIT</button> */}
        </div>
        <form onSubmit={check}>
          <label className="name">
            <span>name:<em>optional</em></span>
            <input name="name" className="name-input" type="text" value={name} onInput={inputname} />
          </label>
          <label className="email">
            <span>Email:<em>* required</em></span>
            <input name="email" className="email-input" type="text" value={email} onInput={inputemail} />
            <span className="emailFailed"></span>
          </label>


          <label className="confirm">
            <span>Confirm Email:<em>* required</em></span>
            <input name="confirm" className="confirm-input" type="text" value={confirm} onInput={inputconfirm} />
            <span className="confirmFailed"></span>
            <span className="notmatchFailed"></span>
          </label>



          <label className="checkbox">
            <span>Please tick the checkbox and leave us a message</span>
            <input type="checkbox" checked={checkbox} onChange={change} />
          </label>
          <div className={box}>
            <p className={box}>We will need an email address if you would like a response. </p>
            <label className="message">
              <input name="message" className={`message-input ${box}`} type="text" value={message} onInput={inputmessage} />
            </label>
            <p>We will need an human-check to screen if you are human or not</p>
            <label className="human">
              <p>human check</p>

              <span>{humanquestion[randomNumber].question}</span>
              <input name="human" className="human-input" type="text" value={answer} onInput={inputanswer} />

            </label>
          </div>
          <button type="submit"  >SUBMIT</button>


        </form>
        <div>

          <div className={success}>
            <p>{savedname}</p>
            <p>{savedemail}</p>
            {/* <p>{savedconfirm}</p> */}
            <p>{savedmessage}</p>
          </div>

          <p>{errorNoemail}</p>
          <p>{errorNoat}</p>
          <p>{errorNomatch}</p>
          <p>{errorNohuman}</p>
          {/* <p>{randomNumber}</p> */}
        </div>



      </div>

    </div>



  );
}

export default Contact;
