import "./css/Contact.css"
import { useState } from "react";
import humanquestion from "./humanquestion.js";

function Contact() {

  // function input(event) {
  //   event.target.getAttribute("href")
  //   console.log(event.target)
  // }

  // console.log(e.tartget.value);

  // const [isLoggedIn, setIsLoggedIn] = useState(false);
  // const [username, setUsername] = useState('');


  // function onNav(event){
  //   event.preventDefault();
  //   const target = event.target.getAttribute("href");
  //   setPage(target);
  // }

  // const humanquestion = [

  //   { question: "please answer 1", answer: "1" },
  //   { question: "please answer 2", answer: "2" },
  //   { question: "please answer 3", answer: "3" },

  // ];

  function inputname(event) {
    setName(event.target.value)
    console.log(event.target.value)
  }

  // function setname() {
  //   setSaved(name)

  // }

  function inputemail(event) {
    setEmail(event.target.value)
    console.log(event.target.value)
  }

  function inputconfirm(event) {
    setConfirm(event.target.value)
    console.log(event.target.value)
  }

  function inputnumber(event) {
    setNumber(event.target.value)
    console.log(event.target.value)
  }

  function setinput() {
    setSavedname(name);
    setSavedemail(email);
    setSavedconfirm(confirm);
    setSavednumber(number);
  }

  const maxvalue = 3
  // const randomNumber = parseInt(Math.random() * number)

  function check(event) {
    event.preventDefault();

    reset()

    if (email) {
      if (email.includes("@")) {
        if (email != confirm)
          seterrorNomatch("should be match")

        else
          setinput()
        // if (number != randomNumber)
        //   return seterrorNohuman('you are not a human');
        // else
        //   return setinput()
      }
      else (
        seterrorNoat("@ is needed")
      )
    }
    else (
      seterrorNoemail("email is needed")
    )
  }

  function reset() {
    seterrorNoemail('');
    seterrorNoat('');
    seterrorNomatch('');
    // setrandomNumber(parseInt(Math.random() * maxvalue))
    // seterrorNohuman('');
  }
  const [randomNumber, setrandomNumber] = useState(parseInt(Math.random() * maxvalue));
  const [name, setName] = useState('');
  const [savedname, setSavedname] = useState('');

  const [number, setNumber] = useState('');
  const [savednumber, setSavednumber] = useState('');

  const [email, setEmail] = useState('');
  const [savedemail, setSavedemail] = useState('');

  const [confirm, setConfirm] = useState('');
  const [savedconfirm, setSavedconfirm] = useState('');

  // emailInput.value.includes(word)

  const [errorNoemail, seterrorNoemail] = useState('');
  const [errorNoat, seterrorNoat] = useState('');
  const [errorNomatch, seterrorNomatch] = useState('');

  const [errorNohuman, seterrorNohuman] = useState('');



  const [boxxxx, setbox] = useState('');
  const [checkbox, setcheckbox] = useState(false)


  function change() {
    setcheckbox(!checkbox)
  }
  let box = "nibuhao"
  if (checkbox) {
    box = "nihao"
    // setbox("nihenbang")
  }



  // const [isOpenButton1, setIsOpen] = useState(false);
  // function Open() {
  //   setIsOpen(!isOpenButton1)
  // }



  return (
    <div className="contact">
      <div className="column">
        <h2>Subscribe to the Latest News.</h2>
        Name and email fields are required
        but we will need an email address if you would like a response. We do not keep or pass on email address.
        Please see our 'Cookies and Privacy' page for more details of our policy on the use of email addresses.
        <div>
          <h3>Name and email fields are required</h3>
          <p>The email should be included the "@"</p>
          <p>We do not keep or pass on email address.</p>
          <p>Please tick the checkbox and leave us a message</p>
          <p>We will need an human-check to screen if you are human or not</p>

        </div>
        <form onSubmit={check}>
          <label className="name">
            <span>name:<em>optional</em></span>
            <input name="name" className="name-input" type="text" value={name} onInput={inputname} />
          </label>
          <label className="email">
            <span>Email:<em>* required</em></span>
            <input name="email" className="email-input" type="text" value={email} onInput={inputemail} />
            <span className="emailFailed"></span>
          </label>


          <label className="confirm">
            <span>Confirm Email:<em>* required</em></span>
            <input name="confirm" className="confirm-input" type="text" value={confirm} onInput={inputconfirm} />
            <span className="confirmFailed"></span>
            <span className="notmatchFailed"></span>
          </label>

          <p>human check</p>

          {/* <label className="human">

            <span>{randomNumber} please input</span>

            <span>{humanquestion[randomNumber].question}</span>
            <input name="human" className="human-input" type="text" value={number} onInput={inputnumber} />

          </label> */}

          {/* <button type="button" onClick={check}>Login</button> */}

          <input type="checkbox" checked={checkbox} onChange={change}/>
          <button type="submit" >SUBMIT</button>
          checkbox

        </form>
        <div>
          <p className={box}>nihenbang </p>
          <p>{savedname}</p>
          <p>{savedemail}</p>
          <p>{savedconfirm}</p>
          <p><strong>{savednumber}</strong></p>
          <p>{errorNoemail}</p>
          <p>{errorNoat}</p>
          <p>{errorNomatch}</p>
          <p>{errorNohuman}</p>
          {/* <p>{randomNumber}</p> */}
        </div>



        {/* <label>
                <span>Username: </span>
                <input value={username} onInput={(e) => setUsername(e.target.value)} />
              </label> */}



        {/* <div className="app">
          <p>Last name seen was {name}</p>
          <label>
            <span>Name: </span>
            <input
              value={name}
              onInput={set}
            />
          </label>
        </div> */}
        {/* <div>
          {isLoggedIn
            ? <div>
              Hello {username}
              <button onClick={() => setIsLoggedIn(false)}>Logout</button>
            </div>
            : <form>
              <label>
                <span>Username: </span>
                <input value={username} onInput={(e) => setUsername(e.target.value)} />
              </label>
              <button type="button" onClick={() => setIsLoggedIn(true)}>Login</button>
            </form>
          }
        </div> */}



        {/* <h2>Subscribe to the Latest Mews.</h2> */}

        {/* <form className="register" action="/subscribe" method="POST">


          <label className="name">
            <span>name:<em>optional</em></span>
            <input name="name" className="name-input" type="text" />
          </label>

          <label className="email">
            <span>Email:<em>* required</em></span>
            <input name="email" className="email-input" type="text" />
            <span className="emailFailed"></span>
          </label>

          <label className="confirm">
            <span>Confirm Email:<em>* required</em></span>
            <input name="confirm" className="confirm-input" type="text" />

            <span className="confirmFailed"></span>
            <span className="notmatchFailed"></span>
          </label>


          <label className="text">
            <span>text:<em>* required</em></span>
            <input name="text" className="text-input" type="text" />

          </label>

          <button className="submit" aria-label="click to subscribe">Subscribe</button>
          <button type="button" className="cancel" aria-label="click to subscribe"> cancel</button>
        </form> */}


      </div>

    </div>



  );
}

export default Contact;
