// import "./css/Privacy.css"
import { useState } from "react";
import "./css/Accessibility.css"
import L4 from "./img/DSC_6157.jpg"
function Accessibility() {


  // const button = document.querySelector('.accordion');

  // button.addEventListener("click", (event) => {
  //   if (!event.target.classList.contains('topic')) {
  //     return;
  //   }
  //   event.target.classList.toggle("topic--open");
  // });

  // "topic--open"

  // const [open,setopen]=useState("topic");

  // function Open(){

  //   setopen("topic topic--open");
  // }




  // const [isOpenButton1, setIsOpen] = useState(false);

  let openClass = 'topic--open';
  // if (!isOpen) {
  //   openClass = '';
  // }

  // return (
  //     <>
  //    <button onClick={ () => setIsOpen( !isOpen ) }>Toggle</button>
  //    <div className={` topic ${openClass}`}> Answer text here</div>
  //     </>
  // );


  const [isOpenButton1, setIsOpen] = useState(false);
  function Open() {
    setIsOpen(!isOpenButton1)
  }

  let openClass1 = 'answer';
  if (!isOpenButton1) {
    openClass1 = 'close';
  }


  const [isOpenButton2, setIsOpen2] = useState(false);
  // function Open() {
  //   setIsOpen2(!isOpenButton2)
  // }

  let openClass2 = 'answer';
  if (!isOpenButton2) {
    openClass2 = 'close';
  }

  const [isOpenButton3, setIsOpen3] = useState(false);
  let openClass3 = 'answer';
  if (!isOpenButton3) {
    openClass3 = 'close';
  }




  return (

    <div className="accessibility" >

      <div className="accordion">
        <h2>Transportation, Accommodation, Ticket Information</h2>
        <p>click each branch to expand</p>
        <button className={` topic ${openClass}`} onClick={() => setIsOpen(!isOpenButton1)} aria-label="click to expand the product introdution Scratching post">
          <span className="topic-title">Getting to the Duxford Battle of Britain Airshow.</span>
          <span className="topic-indicator"></span>
        </button>
        {/* <div className= {` answer ${openClass1}`}> */}
        <div className={openClass1}>

          <h3>By car</h3>
          <p>Duxford is in Cambridgeshire, off Junction 10 of the M11. If you are coming from the north, a signposted alternative is to leave at Junction 11 and take the A10 to Royston, then the A505. This is a much longer route bt it splits the traffic up.</p><p>The Post Code (for Sat Nav) is CB22 4QR but some systems will only recognise the older code CB2 4QR.</p>
          <p>There are links to other route planners in the Travel Advice section.</p>



          <h3>By Train</h3>
          <p>The easiest (but not the closest) train station is Cambridge, which has a direct service from London. </p><p>Alternatives are Royston and Whittlesford Parkway, which is the closest to the show.</p>



        </div>

        <button className={` topic ${openClass}`} onClick={() => setIsOpen2(!isOpenButton2)} aria-label="click to expand the product introdution Cat bell">
          <span className="topic-title">Accommodation near Duxford</span>
          <span className="topic-indicator"></span>
        </button>
        <div className={openClass2}>
          <p class="lead">It is best to book as far as possible in advance. This is not only because nearby hotels and guest houses tend get booked up well before the date of an airshow but also because prices can be better when you book early online.</p>
          <p>The location is already built in to the links but <strong>please check, and change as necessary, the dates, number of rooms and number of guests.</strong></p>
          <p>There are three hotels near IWM Duxford.
            <ul>
              <li><strong> £90 </strong> The John Barleycorn, 3 Moorfield Rd, Duxford, Cambridge CB22 4PP, +441223691411
              </li>
              <li><strong> £141 </strong> The Lodge, Ickleton Rd, Duxford, Cambridge CB22 4RT, +441223755677</li>
              <li><strong> £100 </strong>Holiday Inn Express Cambridge-Duxford,
                42 Station Rd E, Whittlesford, Bridge, Cambridge CB22 4WL, +441223497070</li>
            </ul>
          </p>

        </div>


        <button className={` topic ${openClass}`} onClick={() => setIsOpen3(!isOpenButton3)} aria-label="click to expand the product introdution Cat bell">
          <span className="topic-title">Ticket information</span>
          <span className="topic-indicator"></span>
        </button>
        <div className={openClass3}>
          <p> <strong> £29.75 </strong>
            Adult Standard (aged 16 + years)
            Please add a parking permit to park at the event. Free event shuttle buses are available for all ticket holders.
          </p>
          <p><strong> £27.00 </strong>Standard Adult IWM Member (aged 16 + years)
            Please have your promo code ready to book your tickets. Please add a parking permit to park at the event. Free event shuttle buses are available for all ticket holders.
          </p>
          <p><strong> Free </strong>Standard Child (aged 15 years and under)
            Children go free when booked with a standard adult ticket. All children must be accompanied by an adult.
          </p>
          <p><strong> Free </strong>Personal Assistant
            All visitors with a disability can bring a personal assistant free of charge.
          </p>
          <p><strong >£5.00 </strong>Parking Permit
            Book parking before you proceed to checkout if you intend to park at the event.
          </p>
          <p><strong> £8.00 </strong>Pre-booked Programme
            Pre-purchase your programme to save £2 from on-the-day prices. IWM members receive 10% off programmes and merchandise.
          </p>
          <p className="last"> Tickets will be booked soon, please be sure to buy tickets when they open.</p>
        </div>
      </div>


      <div className="accessibility-photo-div">
        <img className="accessibility-photo" src={L4}
          alt="Piper L-4 is waiting for its turn to take off" />
      </div>

    </div>




  );
}

export default Accessibility;
