    event.preventDefault();

    reset()

    if (email) {
      if (email.includes("@")) {
        if (email === confirm)

          if (checkbox)
            // console.log(answer=="true")
            if (answer=="true")

              if (answer === humanquestion[randomNumber].answer)
                setinput()
