import "./css/Gallery.css"
import "./Gallery.js"
import { useState } from "react";


function Gallery() {

  const [text1,settext1]=useState("open");
  const [text2,settext2]=useState("close");
  const [text3,settext3]=useState("close");
  const [text4,settext4]=useState("close");
  const [text5,settext5]=useState("close");
  const [count, setcount] = useState(0);


  const plusClick = () => {
    setcount(count + 1)

    if (count ==0){
      return (
        settext1("close"),
      settext2("open")) ;

    }
    if (count ==1){
      return (
        settext2("close"),
      settext3("open")) ;

    }

    if (count ==2){
      return (
        settext3("close"),
      settext4("open")) ;

    }
    if (count ==3){
      return (
        settext4("close"),
      settext5("open")) ;

    }
  }

  const reset =() =>{
    setcount(0);
    settext3("close");
    settext1("open");

  }

  console.log(count);

  return (
    <div className="columns">
      <div className={text1}>text111111111</div>
      <div className={text2}>text222222222</div>
      <div className={text3}>text333333333</div>
      <div className={text4}>text444444444</div>
      <div className={text5}>text555555555</div>
      <button className="button" onClick={plusClick}>NEXT</button>
      {(count>4)&& <div>948muio</div>}
      <button className="button" onClick={reset}>RESET</button>
    </div>



  );
}

export default Gallery;
