import "./css/Footer.css"






function Footer({ onNav }) {

  const [showMenu, setShowMenu] = useState(false);
  const list = menu.map(item => {
    return (
      <li className="global-nav_item" key={item.name}>
        <a
          onClick={onNav}
          href={item.path} className="global-nav_link">{item.name}</a>
      </li>
    )
  })

  return (
    <footer>


<nav className={`global-nav ${className}`}>
      <button className="global-nav_button" aria-label={showMenu ? "Close Menu" : "Open Menu"}
        onClick={() => { setShowMenu(!showMenu); }
        }><i className="gg-menu" /></button>
      <ul className={`global-nav_list ${showClass}`}>
        {list}
      </ul>
    </nav>

      <ul className="footer_list">
        <li> <a onClick={onNav} href="/Privacy.html">privavy policy</a></li>
        <li> <a onClick={onNav} href="/Contact.html">contact</a></li>
        <li> <a onClick={onNav} href="/Privacy.html">imperial war museum</a></li>
      </ul>


      <div>©melchiott‘s bakery</div>
      <div>This image is copyright protected under UK copyright Law. </div>

    </footer>
  );
}

export default Footer;
