import "./css/Gallery.css"
import thunderbolt from "./img/DSC_6310.jpg"
import spitfire from "./img/DSC_7408.jpg"
import corsair from "./img/DSC_7403.jpg"
import b17 from "./img/DSC_6842.jpg"
import l3 from "./img/DSC_6463.jpg"
import { useState } from "react";


function Gallery() {

  const [text1, settext1] = useState("open");
  const [text2, settext2] = useState("close");
  const [text3, settext3] = useState("close");
  const [text4, settext4] = useState("close");
  const [text5, settext5] = useState("close");
  const [count, setcount] = useState(0);


  const plusClick = () => {
    setcount(count + 1)

    if (count === 0) {
      return (
        settext1("close"),
        settext2("open"));
    }

    if (count === 1) {
      return (
        settext2("close"),
        settext3("open"));
    }

    if (count === 2) {
      return (
        settext3("close"),
        settext4("open"));

    }
    if (count === 3) {
      return (
        settext4("close"),
        settext5("open"));

    }
  }

  const reset = () => {
    setcount(0);
    settext2("close");
    settext3("close");
    settext4("close");

    settext5("close");
    settext1("open");

  }

  console.log(count);

  return (
    <div className="gallery">
      <h2>Best Time in the Flying Day</h2>


      <div className="buttons">
        <button className="button-next" onClick={plusClick} aria-label="click 'NEXT' to go on the next image">NEXT</button>
        <button className="button-reset" onClick={reset} aria-label=" Click 'RESET' to go back the first image">RESET</button>
      </div>

      {(count > 3) && <div className="buttons-notice">This is the last one. Please click the reset button</div>}
      {(count <= 3) && <div className="buttons-notice">Please click 'NEXT' to go on. Click 'RESET' to go back.</div>}

      <div className="gallery-cards">
        <div className={`gallery-card ${text1}`}>
          <div className="card-div">
          <img className="card-img" src={thunderbolt} alt=" P-47D Thunderbolt ‘549192 / F4-J’ “Nellie” (G-THUN)" />
          </div>
          <p className="card-alt">P-47D Thunderbolt ‘549192 / F4-J’ “Nellie” (G-THUN)</p>
        </div>
        <div className={`gallery-card ${text2}`}>
        <div className="card-div">
          <img className="card-img" src={spitfire} alt="Supermarine Spitfire MK IXb G-ASJV MH434" />
          </div>
          <p className="card-alt">Supermarine Spitfire MK IXb G-ASJV MH434</p>
        </div>

        <div className={`gallery-card ${text3}`}>
        <div className="card-div">
          <img src={corsair} alt="Corsair FG-1D (G-FGID)" />
          </div>
          <p className="card-alt">Corsair FG-1D (G-FGID)</p>
        </div>

        <div className={`gallery-card ${text4}`}>
        <div className="card-div">
          <img src={b17} alt="Boeing B-17G Flying Fortress '124485 - DF-A' (G-BEDF)" />
          </div>
          <p className="card-alt">Boeing B-17G Flying Fortress '124485 - DF-A' (G-BEDF)</p>
        </div>

        <div className={`gallery-card ${text5}`}>
        <div className="card-div">
        </div>
          <img src={l3} alt="Piper J-3C-65 Cub(s)" />
          <p className="card-alt">Piper J-3C-65 Cub(Left:G-BKHG/479766/63-D)</p>
        </div>

      </div>

    </div>



  );
}

export default Gallery;
