import "./css/Gallery.css"
import "./Gallery.js"
import { useState } from "react";


function Gallery() {

  const [text1,settext1]=useState("open");
  const [text2,settext2]=useState("close");
  const [text3,settext3]=useState("close");
  const [text4,settext4]=useState("close");
  const [text5,settext5]=useState("close");
  const [count, setcount] = useState(0);


  const plusClick = () => {
    setcount(count + 1)

    if (count ==0){
      return (
        settext1("close"),
      settext2("open")) ;

    }
    if (count ==1){
      return (
        settext2("close"),
      settext3("open")) ;

    }

    if (count ==2){
      return (
        settext3("close"),
      settext4("open")) ;

    }
    if (count ==3){
      return (
        settext4("close"),
      settext5("open")) ;

    }
  }

  const reset =() =>{
    setcount(0);
    settext5("close");
    settext1("open");

  }

  console.log(count);

  return (
    <div className="gallery">
      <div className="gallery-card">
      <div className={text1}><img src="http://placekitten.com/g/300/300?image=9" alt="" />
        text111111111</div>
      <div className={text2}><img src="http://placekitten.com/g/300/300?image=10" alt="" />
      text222222222</div>
      <div className={text3}><img src="http://placekitten.com/g/300/300?image=11" alt="" />
      text333333333</div>
      <div className={text4}><img src="http://placekitten.com/g/300/300?image=12" alt="" />
      text444444444</div>
      <div className={text5}><img src="http://placekitten.com/g/300/300?image=13" alt="" />
      text555555555</div>


      </div>
      {(count>4)&& <div>948muio</div>}
      <button className="button-next" onClick={plusClick}>NEXT</button>

      <button className="button-reset" onClick={reset}>RESET</button>
    </div>



  );
}

export default Gallery;
