import "./css/Footer.css"
import { useState } from "react";





function Footer({ onNav }) {

  const [showMenu, setShowMenu] = useState(false);

  const showClass = showMenu ? "footer-list-open" : "";

  return (
    <footer>


      <nav className='footer-nav'>
      <button className="footer-nav-button" aria-label={showMenu ? "Close Menu" : "Open Menu"}
        onClick={() => { setShowMenu(!showMenu); }
        }>Useful Link </button>

      <ul className={`footer-list ${showClass}`}>
        <li> <a onClick={onNav} href="/Privacy.html">Privavy Policy</a></li>
        <li> <a onClick={onNav} href="/Contact.html">Contact</a></li>
        <li> <a onClick={onNav} href="/About.html">About</a></li>
      </ul>

    </nav>
{/* 
      <ul className="footer_list">
        <li> <a onClick={onNav} href="/Privacy.html">privavy policy</a></li>
        <li> <a onClick={onNav} href="/Contact.html">contact</a></li>
        <li> <a onClick={onNav} href="/Privacy.html">imperial war museum</a></li>
      </ul> */}


      <div>©melchiott‘s bakery</div>
      <div>Images are copyright protected under UK copyright Law. </div>

    </footer>
  );
}

export default Footer;
