import "./css/Contact.css"
import { useState } from "react";
import humanquestion from "./humanquestion.js";

function Contact({ thanksfunction }) {

  // function input(event) {
  //   event.target.getAttribute("href")
  //   console.log(event.target)
  // }

  // console.log(e.tartget.value);

  // const [isLoggedIn, setIsLoggedIn] = useState(false);
  // const [username, setUsername] = useState('');


  // function onNav(event){
  //   event.preventDefault();
  //   const target = event.target.getAttribute("href");
  //   setPage(target);
  // }

  // const humanquestion = [

  //   { question: "please answer 1", answer: "1" },
  //   { question: "please answer 2", answer: "2" },
  //   { question: "please answer 3", answer: "3" },

  // ];

  function inputname(event) {
    setName(event.target.value)
    console.log(event.target.value)
  }

  // function setname() {
  //   setSaved(name)

  // }

  function inputemail(event) {
    setEmail(event.target.value)
    console.log(event.target.value)
  }

  function inputconfirm(event) {
    setConfirm(event.target.value)
    console.log(event.target.value)
  }

  function inputmessage(event) {
    setMessage(event.target.value)
    console.log(event.target.value)
  }

  function inputanswer(event) {
    setAnswer(event.target.value)
    console.log(event.target.value)
  }

  // let setting ="false"
  function setinput() {
    setSavedname(name);
    setSavedemail(email);
    setSavedconfirm(confirm);
    setSavednumber(number);
    setSavedmessage(message);
    setisthanks(true)
    // {thanksfunction};

  }
  //  setting = "true"
  // console.log(setting)
  // }

  // if(setting)
  // {
  //   {thanksfunction}
  // }



  // const randomNumber = parseInt(Math.random() * number)

  function check(event) {
    event.preventDefault();

    reset()

    if (email) {
      if (email.includes("@")) {
        if (email === confirm)

          if (checkbox)
            // console.log(answer=="true")
            if (answer)

              if (answer === humanquestion[randomNumber].answer)
                setinput()
              else
                return (setfailed("failed"),
                  seterrorNohuman(''),
                  seterrorNohuman('Sorry, the answer is not correct')
                )


            else
              return (setfailed("failed"),
                seterrorNohuman('Please the answer for the human-check')
              )


          else setinput()
        // else
        //   setinput()
        // if (answer != humanquestion[randomNumber].answer)
        //   return seterrorNohuman('you are not a human');
        else
          return (setfailed("failed"),
            seterrorNomatch(" The email address should be match")
          )

      }
      else
        return (
          setfailed("failed"),
          seterrorNoat("Please inpur your correct email. @ is needed")
        )
    }
    else
      return (
        setfailed("failed"),
        seterrorNoemail("You need to type your email address")



      )
  }

  // const [isDisable, setDisable] = useState('1');

  function reset() {
    seterrorNoemail('');
    seterrorNoat('');
    seterrorNomatch('');
    setrandomNumber(parseInt(Math.random() * maxvalue))
    seterrorNohuman('');
    // setfailed("failed-closed")
    // setDisable("false")
  }
  let maxvalue = 6
  const [randomNumber, setrandomNumber] = useState(parseInt(Math.random() * maxvalue));
  const [name, setName] = useState('');
  const [savedname, setSavedname] = useState('');

  const [number, setNumber] = useState('');
  const [savednumber, setSavednumber] = useState('');


  const [answer, setAnswer] = useState('');
  const [savedanswer, setSavedanswer] = useState('');


  const [email, setEmail] = useState('');
  const [savedemail, setSavedemail] = useState('');

  const [confirm, setConfirm] = useState('');
  const [savedconfirm, setSavedconfirm] = useState('');


  const [message, setMessage] = useState('');
  const [savedmessage, setSavedmessage] = useState('');

  // emailInput.value.includes(word)
  const [failed, setfailed] = useState('failed-closed');
  const [success, setsuccess] = useState('');

  const [errorNoemail, seterrorNoemail] = useState('');
  const [errorNoat, seterrorNoat] = useState('');
  const [errorNomatch, seterrorNomatch] = useState('');

  const [errorNohuman, seterrorNohuman] = useState('');



  const [boxxxx, setbox] = useState('');
  const [checkbox, setcheckbox] = useState(false)


  function change() {
    setcheckbox(!checkbox)
  }



  let box = "close"
  if (checkbox) {
    box = "open"
    // setbox("nihenbang")
  }

  <textarea id="message1" class="form-control" placeholder="Messag" maxlength="500"></textarea>


  const [isthanks, setisthanks] = useState(false);
  let content

  if (isthanks) {
    content = (

      <div><h3>Thank you</h3>
        <p>You have submitted successfully </p></div>

    );
  }

  else {
    content = (

      <div className="contact-form">

        <div className="contact-rule">
          <p>Name is optional but email fields are required</p>
          <p>The email should be included the "@"</p>
          <p>We do not keep or pass on email address.</p>
        </div>

        <form onSubmit={check} className="form">
          <label className="name">
            <span>Name:</span>
            <input name="name" className="name-input" type="text" value={name} placeholder="optional" onInput={inputname} />
          </label>
          <label className="email">
            <span>Email:</span>
            <input name="email" className="email-input" type="text" value={email} placeholder="required" onInput={inputemail} />
            <span className="emailFailed"></span>
          </label>


          <label className="confirm">
            <span>Confirm Email:</span>

            <input name="confirm" className="confirm-input" type="text" placeholder="required" value={confirm} onInput={inputconfirm} />
            <span className="confirmFailed"></span>
            <span className="notmatchFailed"></span>
          </label>



          <label className="checkbox">
            <span>Please tick the checkbox and leave us a message</span>
            <input type="checkbox" className="checkbox-button" checked={checkbox} onChange={change} />
          </label>
          <div className={box}>

            <label className="message">
              <p>We will need an email address if you would like a response. </p>
              <textarea id="message1" className="form-control" placeholder="Please input the message here" value={message} onInput={inputmessage}></textarea>

              {/* <input name="message" className={`message-input ${box}`} type="text" value={message} onInput={inputmessage} /> */}
            </label>


            <label className="human">
              <p>We will need an human-check to screen if you are human or not</p>
              <p>The answer is 5 CAPITAL LETTERS or Numbers. This is the answer <strong>{humanquestion[randomNumber].answer}</strong></p>             
              <span>{humanquestion[randomNumber].question}</span>
              <input name="human" className="human-input" type="text" value={answer} onInput={inputanswer} />

            </label>
          </div>
          <button type="submit" className="form-button"  >SUBMIT</button>


        </form>

      </div>);
  }


  return (
    <div className="contact">
      {/* <div className="column"> */}
      <h2>Subscribe to the Latest News.</h2>
      {/* <button type="submit"  onClick={thanksfunction} >SUBMIT</button> */}

      {/* {content-rule} */}
      {content}

      {/*    
      <form onSubmit={check} className="form">
        <label className="name">
          <span>Name:</span>
          <input name="name" className="name-input" type="text" value={name} placeholder="optional" onInput={inputname} />
        </label>
        <label className="email">
          <span>Email:</span>
          <input name="email" className="email-input" type="text" value={email} placeholder="required" onInput={inputemail} />
          <span className="emailFailed"></span>
        </label>


        <label className="confirm">
          <span>Confirm Email:</span>

          <input name="confirm" className="confirm-input" type="text" placeholder="required" value={confirm} onInput={inputconfirm} />
          <span className="confirmFailed"></span>
          <span className="notmatchFailed"></span>
        </label>



        <label className="checkbox">
          <span>Please tick the checkbox and leave us a message</span>
          <input type="checkbox" className="checkbox-button" checked={checkbox} onChange={change} />
        </label>
        <div className={box}>

          <label className="message">
          <p>We will need an email address if you would like a response. </p>
          <textarea id="message1" class="form-control" placeholder="Please input the message here, maxlength is 500 words" maxlength="500" value={message} onInput={inputmessage}></textarea>

          </label>


          <label className="human">
            <p>We will need an human-check to screen if you are human or not</p>
            <p>The answer is 5 capital English letters</p>
            <p>{humanquestion[randomNumber].answer}</p>
            <span>{humanquestion[randomNumber].question}</span>
            <input name="human" className="human-input" type="text" value={answer} onInput={inputanswer} />

          </label>
        </div>
        <button type="submit" className="form-button"  >SUBMIT</button>


      </form> */}

      <div className={failed}>
        <p >{errorNoemail}</p>
        <p>{errorNoat}</p>
        <p>{errorNomatch}</p>
        <p>{errorNohuman}</p>
      </div>


      {/* <div className="success">
        <p>This is your name: {savedname}</p>
        <p>This is your email address you submitted: {savedemail}</p>
        <p>{savedconfirm}</p>
        <p>This is your message you submitted: {savedmessage}</p>
      </div> */}


      {/* <p>{randomNumber}</p> */}

      {/* <input name="message" className={`message-input ${box}`} type="text" value={message} onInput={inputmessage} /> */}


      {/* </div> */}

    </div>



  );
}

export default Contact;
