import "./css/Footer.css"
import { useState } from "react";





function Footer({ onNav }) {

  const [showMenu, setShowMenu] = useState(false);

  const showClass = showMenu ? "footer-list-open" : "";

  return (
    <footer>


      <nav className='footer-nav'>
        <button className="footer-nav-button" aria-label={showMenu ? "Close Menu" : "Open Menu"}
          onClick={() => { setShowMenu(!showMenu); }
          }>Useful Link </button>

        <ul className={`footer-list ${showClass}`}>
          <li> <a onClick={onNav} href="/Privacy.html" className="footer-nav-link">Privavy Policy</a></li>
          <li> <a onClick={onNav} href="/Contact.html" className="footer-nav-link">Contact</a></li>
          <li> <a onClick={onNav} href="/About.html" className="footer-nav-link">About</a></li>
        </ul>

      </nav>

      <div>©melchiott‘s bakery</div>
      <div>Images are copyright protected under UK copyright Law. </div>

    </footer>
  );
}

export default Footer;
