"use strict";
(function () {
  const accordion1 = document.querySelector('.card1');
  const accordion2 = document.querySelector('.card2');
  const accordion3 = document.querySelector('.card3');

  accordion1.addEventListener('click', function () {
    this.classList.toggle('active')
    console.log("1 click");
  })
  accordion2.addEventListener('click', function () {
    this.classList.toggle('active')
    console.log("2 click");
  })
  accordion3.addEventListener('click', function () {
    this.classList.toggle('active')
    console.log("3 click");
  })

  const button = document.querySelector('.accordion');

  button.addEventListener("click", (event) => {
    if (!event.target.classList.contains('topic')) {
      return;
    }
    event.target.classList.toggle("topic--open");
  });

})();
